using UnityEngine;using System.Collections;public class PatrolState : FSMState{    private EnemyController enemyController;    public PatrolState(EnemyController enemyController)    {        this.enemyController = enemyController;        stateID = FSMStateID.Patrolling;    }    public override void Reason(Transform player, Transform npc)    {        if (Vector3.Distance(npc.position, player.position) <= enemyController.attackDistance)        {            Debug.Log("Switch to Attack State");            enemyController.SetTransition(Transition.SawPlayer);        }    }    public override void Act(Transform player, Transform npc)    {    }}